sqlMap.select:
SELECT * FROM SEC_STAFF WHERE STATUS != 'DELETED' AND STAFF_ID IN ( SELECT STAFF_ID FROM SEC_STAFF_DEPARTMENT_ROLE WHERE ROLE_ID IN ( SELECT ROLE_ID FROM SEC_ROLE WHERE ROLE_ID IN ( SELECT DISTINCT ROLE_ID FROM sec_role_resource_operation a,sec_resources b,SEC_OPERATION c WHERE a.resource_id=b.resource_id and RESOURCE_KEY=#resourceKey# and c.resource_id=b.resource_id and c.operation_key = a.operation_key
c.OPERATION_KEY = trim(#operationKey#)
) ) )
