package com.wml.springboot.auth.entity;import java.io.Serializable;import java.util.HashMap;import java.util.Map;/** * 公用实体 * <pre> * <b>Title：</b>BaseEntity.java<br/> * <b>@author：</b>WML<br/> * <b>@date：</b>2016年11月9日 - 上午9:09:57<br/>   * <b>@version V1.0</b></br/> * <b>Copyright (c) 2016 ASPire Tech.</b>    * </pre> */public class BaseEntity implements Serializable {		private static final long serialVersionUID = 5869181224979219137L;		@SuppressWarnings({ "rawtypes", "unchecked" })	private Map<String, Object> others = new HashMap();	public Map<String, Object> getOthers() {		return this.others;	}	public void setOthers(Map<String, Object> params) {		this.others = params;	}	public void addOtherField(String key, Object value) {		this.others.put(key, value);	}}