package com.wml.springboot.auth.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import java.util.ArrayList;import java.util.Date;import java.util.List;/** * 部门 * <pre> * <b>Title：</b>Department.java<br/> * <b>@author：</b>WML<br/> * <b>@date：</b>2016年11月9日 - 上午9:11:51<br/>   * <b>@version V1.0</b></br/> * <b>Copyright (c) 2016 ASPire Tech.</b>    * </pre> */@SuppressWarnings({ "unchecked", "rawtypes" })public class Department extends BaseEntity {	public static final String ROOT_DEPARTMENT_NAME = "根组织";	public static final String PATH = "path";	public static final String PATH_SEPARATOR = "/";	public static final String ZW_SYS_ADMIN = "管理员";	public static final String ZW_SP = "SP";	public static final String SYS_ADMIN = "SYS_ADMIN";	public static final String SP = "SP";	public static final long ROOT_DEPARTMENT_ID = -999L;	private static final long serialVersionUID = 3829319798451942102L;		/**	 * 组织id 	 */	private Long departmentId;		/**	 * 组织名称	 */	private String departmentName;		/**	 * 组织描述	 */	private String departmentDesc;		/**	 * 父级id	 */	private Long parentId;		/**	 * 邮箱	 */	private String email;		/**	 * 组织地址	 */	private String address;		/**	 * 是否可以创建下级组织	 */	private String addSub;	/**	 * 创建用户	 */	@JsonIgnore	private String createUser;	/**	 * 创建时间	 */	@JsonIgnore	private Date createDate;	/**	 * 组织最后修改时间	 */	@JsonIgnore	private Date lastUpdateDate;		/**	 * 管理域：SP、SYS_ADMIN	 */	private String domain;	/**	 * 用户列表	 */	@JsonIgnore	private List<Staff> staffs;	/**	 * 角色列表	 */	@JsonIgnore	private List<Role> roles;	/**	 * 子部门列表	 */	@JsonIgnore	private List<Department> subDepartments;	/**	 * 父级组织	 */	@JsonIgnore	private Department parent;	@JsonIgnore	private List<Department> childs = new ArrayList();	public Long getDepartmentId() {		return this.departmentId;	}	public void setDepartmentId(Long departmentId) {		this.departmentId = departmentId;	}	public String getDepartmentName() {		return this.departmentName;	}	public void setDepartmentName(String departmentName) {		this.departmentName = departmentName;	}	public String getDepartmentDesc() {		return this.departmentDesc;	}	public void setDepartmentDesc(String departmentDesc) {		this.departmentDesc = departmentDesc;	}	public Long getParentId() {		return this.parentId;	}	public void setParentId(Long parentId) {		this.parentId = parentId;	}	public String getEmail() {		return this.email;	}	public void setEmail(String email) {		this.email = email;	}	public String getAddress() {		return this.address;	}	public void setAddress(String address) {		this.address = address;	}	public String getAddSub() {		return this.addSub;	}	public void setAddSub(String addSub) {		this.addSub = addSub;	}	public String getCreateUser() {		return this.createUser;	}	public void setCreateUser(String createUser) {		this.createUser = createUser;	}	public Date getCreateDate() {		return this.createDate;	}	public void setCreateDate(Date createDate) {		this.createDate = createDate;	}	public Date getLastUpdateDate() {		return this.lastUpdateDate;	}	public void setLastUpdateDate(Date lastUpdateDate) {		this.lastUpdateDate = lastUpdateDate;	}	public String getDomain() {		return this.domain;	}	public void setDomain(String domain) {		this.domain = domain;	}	public List<Staff> getStaffs() {		return this.staffs;	}	public void setStaffs(List<Staff> staffs) {		this.staffs = staffs;	}	public List<Role> getRoles() {		return this.roles;	}	public void setRoles(List<Role> roles) {		this.roles = roles;	}	public void addRole(Role role) {		if (this.roles == null) {			this.roles = new ArrayList();		}		this.roles.add(role);	}	public List<Department> getSubDepartments() {		return this.subDepartments;	}	public void setSubDepartments(List<Department> subDepartments) {		this.subDepartments = subDepartments;	}	public void addSubDepartment(Department department) {		if (this.subDepartments == null) {			this.subDepartments = new ArrayList();		}		this.subDepartments.add(department);	}	public boolean isRootDepartment() {		return this.parentId == null;	}	public Department getParent() {		return this.parent;	}	public void setParent(Department parent) {		this.parent = parent;	}	public List<Department> getChilds() {		return this.childs;	}	public void setChilds(List<Department> childs) {		this.childs = childs;	}	public void addChild(Department dep) {		this.childs.add(dep);	}	@Override	public String toString() {		return "Department [departmentId=" + departmentId + ", departmentName="				+ departmentName + ", departmentDesc=" + departmentDesc				+ ", parentId=" + parentId + ", email=" + email + ", address="				+ address + ", addSub=" + addSub + ", createUser=" + createUser				+ ", createDate=" + createDate + ", lastUpdateDate="				+ lastUpdateDate + ", domain=" + domain + ", staffs=" + staffs				+ ", roles=" + roles + ", subDepartments=" + subDepartments				+ ", parent=" + parent + ", childs=" + childs + "]";	}	}