mapper.select:
select

from sec_operation
where OPERATION_ID = #{operationId, jdbcType=BIGINT}

mapper.delete:
delete from sec_operation where 1 = 1

	and OPERATION_ID in

		#{tag}

mapper.insert:
insert into sec_operation(OPERATION_ID, RESOURCE_ID, OPERATION_KEY, OPERATION_NAME,
OPERATION_DESC, DEPEND_KEY, DEPEND_BY_KEY,
METADATA_ID, DOMAIN, ORDER_KEY
)
values (#{operationId, jdbcType=BIGINT}, #{resourceId, jdbcType=BIGINT}, #{operationKey, jdbcType=VARCHAR}, #{operationName, jdbcType=VARCHAR},
#{operationDesc, jdbcType=VARCHAR}, #{dependKey, jdbcType=VARCHAR}, #{dependByKey, jdbcType=VARCHAR},
#{metadataId, jdbcType=VARCHAR}, #{domain, jdbcType=VARCHAR}, #{orderKey, jdbcType=INTEGER}
)
