mapper.select:
select

from sec_role
where ROLE_ID = #{roleId, jdbcType=BIGINT}

mapper.delete:
delete from sec_role where 1 = 1

	and ROLE_ID in

		#{tag}

mapper.insert:
insert into sec_role(ROLE_ID, ROLE_NAME, ROLE_DESC, CREATE_USER,
CREATE_DATE, CAN_MODIFY, LAST_UPDATE_DATE,
ROLE_KEY, AUTO_ASSIGN, visible
)
values (#{roleId, jdbcType=BIGINT}, #{roleName, jdbcType=VARCHAR}, #{roleDesc, jdbcType=VARCHAR}, #{createUser, jdbcType=VARCHAR},
#{createDate, jdbcType=TIMESTAMP}, #{canModify, jdbcType=INTEGER}, #{lastUpdateDate, jdbcType=TIMESTAMP},
#{roleKey, jdbcType=VARCHAR}, #{autoAssign, jdbcType=INTEGER}, #{visible, jdbcType=INTEGER}
)
